import products from './models/testmodel.js';
import express from 'express';
const app = express();
const port = 8000;


app.use(express.json());

app.get('/', (req, res) => {
    res.send('Hello, World!');
});

app.get('/products', (req, res) => {
    res.json(products);
});




app.listen(port, () => {
    console.log('Server is running on http://localhost:${port}');
});